function updateExchangeRates() {
    var rates = JSON.parse((new WebResource('http://openexchangerates.org/latest.php')).get().content);
    this.exchangeRates = rates.rates;
    this.symbols = [];

    for (var prop in this.exchangeRates) {
        this.symbols.push(prop);
    }
    this.get('from').setList(this.symbols);
    this.get('to').setList(this.symbols);
}

