function convert() {
    var input = parseFloat(this.get('input').textString),
        outputDollars = this.toDollar(this.get('from').getLabel()) * input,
        output = outputDollars * this.fromDollar(this.get('to').getLabel());

    this.get('output').textString = output.toFixed(4);
}
