function rebuildButtons() {  //this.rebuildButtons();
    this.submorphs.select(function (m) { return m instanceof lively.morphic.Button })
        .forEach(function (m) { m.remove() });

    var n = this.cylinders.length;
    var label = (n==1) ? '1 cylinder' : n.toString() + ' cylinders';
    var b = this.addMorph(new lively.morphic.Button (new Rectangle(50, 440, 140, 20), label));
    b.menuItems = Array.range(1,9).collect( function(i) {
            return [i.toString(), this, 'makeCylinders', i]; }.bind(this));
    b.addScript(function onMouseUp(evt) {
            lively.morphic.Menu.openAt(evt.getPosition(), 'Number of cylinders',   this.menuItems); });

    var label = (this.alternate ? 'alternate' : 'sequential') + ' ignition';
    var b = this.addMorph(new lively.morphic.Button (new Rectangle(50, 490, 140, 20), label));
    b.addScript(function onMouseUp(evt) { 
            this.owner.setAlternateTiming(!this.owner.alternate); });

    var label =  this.running ? 'running' : 'stopped';
    var b = this.addMorph(new lively.morphic.Button (new Rectangle(320, 440, 70, 20), label));
    b.addScript(function onMouseUp(evt) {
            this.owner.setRunning(!this.owner.running); });

if (this.running) {
    var label =  (this.stepTime==1) ? 'fast' : 'slow';
    var b = this.addMorph(new lively.morphic.Button (new Rectangle(320, 490, 70, 20), label));
    b.addScript(function onMouseUp(evt) {
            var isSlow = this.owner.stepTime != 1;
            this.owner.setStepTime(isSlow ? 1 : this.owner.normalSpeed);
            this.setLabel(isSlow ? 'fast' : 'slow'); });
} else {
    var b = this.addMorph(new lively.morphic.Button (new Rectangle(320, 490, 70, 20), 'step'));
    b.addScript(function onMouseUp(evt) { this.owner.doStep(); });
    }
}

