    this.next = new Function('obj', 'sim', lines.join("\n"));
}

54.source:
function step() {
    var newVals = this.submorphs.map(function(obj) {
        var force = this.next(obj, this);
        var vals = {
            v: obj.v.addPt(force),
            p: obj.p.addPt(obj.v)
        };
        var bounds = lively.pt(0, 0).extent(this.getExtent().subPt(obj.getExtent()));
        if (!bounds.containsPoint(vals.p)) {
            vals.p = bounds.constrainPt(vals.p);
            vals.v = vals.v.fastR() > 4 ? vals.v.scaleBy(-.4) : lively.pt(0,0);
        }
        return vals;
    }, this);
    for (var i = 0; i < this.submorphs.length; i++) {
        var m = this.submorphs[i], n = newVals[i];
        m.setPosition(n.p);
        m.p = n.p;
        m.v = n.v;
    }
}

