62.source:
function addExampleMorphs(number) {
    for (var i = 0; i < number; i++) {
        var extent = lively.Point.random(lively.pt(20, 20)).addXY(20, 20);
        var maxPos = this.getExtent().subPt(extent);
        var pos = lively.Point.random(maxPos);
        var morph = new lively.morphic.Box(pos.extent(extent));
        morph.setBorderWidth(1);
        morph.setBorderColor(Color.black);
        morph.setFill(Color.random());
        morph.v = lively.pt(0, 0);
        morph.p = pos;
        this.addMorph(morph);
    }
}

