function finishLoading(res){  
    if (!res.status.isDone()) return;
    if (res.isExisting) {
        this.get('FileContent').setTextString(res.content);
          // ugly, ugly :-)
        (function() {
            this.get('FileContent').highlightJavaScriptSyntax()
        }).bind(this).delay(1)

        return
    } else if (res.getURL().isLeaf()) {
        this.askToCreateFile(res);
    } else {
        alert('Cannot open/create document at ' + res.getURL());
    }
}

