function saveFile() {
    var webR = this.createWebResource();
    webR
        .beAsync()
        .createProgressBar()
        .statusMessage('Successfully saved ' + webR.getURL(), 
            'Error saving ' + webR.getURL(), true)
        .put(this.get('FileContent').textString);
}

