function showAsLoading(bool) {
    if (!bool) {
        this.loadingScreen && this.loadingScreen.remove();
        this.loadingScreen = null;
    } else {
        if (this.loadingScreen) return;
        var morph = lively.morphic.Morph.makeRectangle(this.get('FileContent').bounds());
        morph.applyStyle({fill: Color.gray.withA(0.6)});
        this.loadingScreen = this.addMorph(morph);
    }
}

