function restartServer() {
    require(['server.nodejs.WebInterface']).toRun(function() {
        var master= this.getMasterServerInterface(),
            serverName = this.getServerName()
        alertOK('restart ' + serverName)
        master.updateAndRestart(serverName);
        this.showLog.bind(this).delay(1); // give it some time
    }.bind(this))
}

