function searchUp() {
   this.searchIndex -= 1;
   this.searchIndex = Math.max(this.searchIndex, 0);
   this.gotoIndex(this.searchIndex)
}

328.source:
function searchDown() {
   this.searchIndex += 1;
   this.searchIndex = Math.min(this.searchIndex, this.searchResult.length - 1)
   this.gotoIndex(this.searchIndex)
}

344.source:
function gotoIndex(index) {
    if (this.target && this.target.hideSearchActivity)
        this.target.hideSearchActivity();
    var morph = this.searchResult[index];
    this.target = morph;
    $world.setScroll(this.globalBounds());
    if (morph.findString) morph.findString(this.searchString);
    this.updateResultLabel(index);
    this.alignToWindowBounds()
}

