function reset() {
    this.doNotSerialize = ['target', 'searchString', 'searchResult', 'searchIndex']
    this.get('ResultLabel').textString = '0 of 0'
    this.get('SearchField').textString = ''
    this.isEpiMorph = false; // reset it for development
}

352.source:
function performSearch(s) {
    var found = [];
    $world.withAllSubmorphsDo(function(ea) {
        if (ea.textString && ea.textString.match(s) && (ea.owner !== this))
            found.push(ea)
    }, this)
    found = found.sortBy(function(ea) {
        return ea.globalBounds().topLeft()
    })
    this.searchString = s;
    this.searchResult = found;
    this.searchIndex = 0;
    this.updateResultLabel(0)
}

