function saveChanges() {
    // gather annotation informations
    var initials = this.get("Initials").getTextString();
    var timestamp = new Date();
    var comment = this.get("Comment").getTextString();
    var user; 
    if (this.annotationPin.world() && this.annotationPin.world().getUserName && 
        this.annotationPin.world().getUserName()) {
        user = this.annotationPin.world().getUserName();
    } 

    // reset the annotation object
    this.annotation = {
        initials : initials,
        author : user ? user : initials,
        timestamp : timestamp,
        comment : comment
    };

    // update the annotation morph
    this.get("Timestamp").setTextString(this.annotation.timestamp);
    this.annotationPin.get("AuthorLabel").setTextString(this.annotation.initials);
    this.restoreAnnotation();
}

