function startPollMessages() {
    var pane = this;
    var cdb = this.getDB()
    cdb.startPollingChanges(function(update) {
        if (update.id.startsWith('_design/'))
            return;
        var doc = cdb.open(update.id);
        pane.addMessage(doc)
    });
}

