function reset() {
    this.getPartsBinMetaInfo().requiredModules = ['apps.CouchDB'];
    this.doNotSerialize = ['cdb']
    this.get('InputText').isInputLine = true;
    this.get('ChatText').textString = ''

    // this.owner.minExtent = this.owner.getExtent()
}

97.source:
function sendMessage(msg) {
    this.getDB().saveUsingUpdate({
        message: msg
    }, 'chat/sendMessage');
    this.get('InputText').setTextString('')
    this.get('InputText').focus();
    this.get('InputText').setSelectionRange(0,0);
}

101.source:
function addMessage(doc) {
    this.get('ChatText').textString += doc.sent_by  + ": " + doc.message + '\n';
    this.get('ChatText').scrollToBottom()
}

