function fetchAndDisplayQandA() {
	var worldPath = URL.source.relativePathFrom(URL.codeBase);
	var repoName = URL.codeBase.filename(); // FIX ME: what is the real repo name?

	var docs;
	if (this.isGlobal)
		docs = this.db.view('questions/all');
	else
		docs = this.db.view('questions/by_world', { key: repoName + worldPath });
	var listItems = [];
	if (!docs.error && (docs.total_rows > 0)) {
		listItems = docs.rows.collect(function(doc) {
			return this.listItemsForDocs(doc);
		}, this).flatten();
	}
	this.questionList.updateList(
		listItems.sortBy(function(item) { return item.value.time_asking; }).reverse()
	);
	this.questionList.formatList();
}

