function generateQandA(doc) {
	var d1 = new Date(doc.time_asking);
	var dStr = '' + (d1.getMonth() + 1) + '/' + d1.getDate() + '/' + d1.getFullYear() + ', '
		+ ((d1.getHours() < 10) ? '0' : '') + d1.getHours() + ':'
		+ ((d1.getMinutes() < 10) ? '0' : '') + d1.getMinutes() + ':'
		+ ((d1.getSeconds() < 10) ? '0' : '') + d1.getSeconds();

	var richText = new lively.morphic.RichText(
		doc.question
		+ '\n' + doc.user_asking + ' -- ' + dStr
		+ '\n\n' + (doc.answer ? doc.answer : '')
	);

	richText.emphasize({ style: 'bold' }, 0, doc.question.length - 1);
	richText.emphasize({ align: 'right', style: 'italic' }, doc.question.length, doc.question.length + dStr.length + doc.user_asking.length + 4);

	return richText;
}

