function saveAnswer() {
	var changed = false;

	var d = new Date();
	var res = new WebResource(Config.userNameURL);
	var user = JSON.unserialize(res.get().content);

	var doc = this.questionList.selection;
	doc.time_answered = d.valueOf();
	doc.user_answered = user;

	var lines = this.QandAText.textString.split('\n');
        var question = lines[0];
	if (doc.question != question) {
		doc.question = question; // TODO: ask whether to also save changes in the question?
		this.questionList.itemList[this.questionList.selectedLineNo].string = question;
		this.questionList.updateList(this.questionList.itemList);
		changed = true;
	}

	var answerLines = lines.slice(2);
	var startLine;
	answerLines.find(function(line, idx) {
		return line.trim() != '' && (startLine = idx);
	});
	if (startLine) {
		doc.answer = answerLines.slice(startLine).join('\n');
		changed = true;
	}

	if (changed) {
		this.db.save(doc);
		this.questionList.formatList();
		alertOK('Saved!')
	}
}

