function saveQuestion(question) {
	var d = new Date();
	var worldPath = URL.source.relativePathFrom(URL.codeBase);
	var repoName = URL.codeBase.filename(); // FIX ME: what is the real repo name?
	if (repoName.endsWith('/'))
		repoName = repoName.substr(0, repoName.length - 1);

	var res = new WebResource(Config.userNameURL);
	var user = JSON.unserialize(res.get().content);

	var dbObj = {
		repository: repoName,
		world: worldPath,
		question: question,
		user_asking: user,
		time_asking: d.valueOf(),
		answer: null,
		time_answered: null,
		user_answered: null
	};

	console.log([dbObj]);
	this.db.save(dbObj);
	this.fetchAndDisplayQandA();
}

