function search(searchStr) {
	searchStr = searchStr.toLowerCase();

	var docs;
	docs = this.db.view('search/lowerSubstring',
		{ startkey: searchStr, endkey: searchStr + '\u9999' });

	var listItems = [];
	if (!docs.error && (docs.total_rows > 0)) {
		docs.rows = docs.rows.reduce(function(res, obj) {
			return (res.find(function(obj2) { return obj.id == obj2.id; }) != null
				? res
				: res.concat(obj));
		}, []);
		listItems = docs.rows.collect(function(doc) {
			return this.listItemsForDocs(doc);
		}, this).flatten();
	}
	this.questionList.updateList(listItems);
	this.questionList.formatList();
}

