function updateQuestionPane(doc) {
	var pane = this.QandAText;

	if (!doc || doc == '-----') {
		pane.setTextString('---');
	} else {
		this.setTextInPane(this.generateQandA(doc), pane);
		this.worldURL.setTextString(doc.repository + '/' + doc.world);
		this.worldURL.emphasizeAll({
			// color: "blue",
			uri: URL.codeBase.withFilename('../' + doc.repository + '/' + doc.world).withRelativePartsResolved().toString()
		});
	}
}
