// Get the source of that page asynchronously
// we use an updater for the output because content can be written multiple
// times while loading, we first want to trigger the alert when the request is done
var webR = new WebResource(URL.source).beAsync();
connect(webR, 'content', this.world(), 'alert', {
    updater: function($upd, content) {
        if (this.sourceObj.status.isDone())
            $upd('Get finished, loaded ' + (content.length/1024) + ' kbytes') }})
webR.get();
