function updateLabel() {
	// observe name changes in owner
	if (this.owner !== this.oldOwner) {
		if (this.oldOwner)
			disconnect(this.oldOwner, 'name', this, 'updateLabel')
		if (this.owner)
			connect(this.owner, 'name', this, 'updateLabel')
		this.oldOwner = this.owner
	}
	var name = (this.owner ? this.owner.name : "no owner")
	if (this.textString != name)
	this.setTextString(name)
}

