function applyTo(morph) {
    morph.addScript(function connectBox2D() {
    this.getGrabShadow = function(){ return new lively.morphic.Morph; }
    this.box2dWorld = this.world().box2dWorld;
    var ext = this.getExtent();   
    this.adjustOrigin(pt(ext.x/2, ext.y/2));
    this.recreatePhysicsVersion(ext);
    connect(this, "rotation", this.physicsVersion, "SetAngle");
    connect(this, "extent", this, "recreatePhysicsVersion");
    connect(this, "owner", this, "onOwnerChange");    
    });

    morph.addScript(function disconnectBox2D() {
    disconnect(this, "rotation", this.physicsVersion, "SetAngle");
    disconnect(this, "extent", this, "recreatePhysicsVersion");  
    this.box2dWorld = null;
    this.physicsVersion = null;
    });

    morph.addScript(function onDrag(evt) {
    if (!(this.box2dWorld && this.world().running))
        return;

    var b2Vec2 = Box2D.Common.Math.b2Vec2
        , b2AABB = Box2D.Collision.b2AABB
        , b2BodyDef = Box2D.Dynamics.b2BodyDef
        , b2Body = Box2D.Dynamics.b2Body
        , b2MouseJointDef =  Box2D.Dynamics.Joints.b2MouseJointDef
        , p = new b2Vec2(evt.x, evt.y);

    if(this.mousePressed && !this.mouseJoint)
    {    
        var def = new b2MouseJointDef();
        def.bodyA = this.world().walls[0];
        def.bodyB = this.physicsVersion;;
        def.target = p;
        def.collideConnected = true;
        def.maxForce = 100000 * this.physicsVersion.GetMass();
        def.dampingRatio = 0;

        this.mouseJoint = this.box2dWorld.CreateJoint(def);
        this.physicsVersion.SetAwake(true);
    }
    if(this.mouseJoint)
        this.mouseJoint.SetTarget(p);
    });

    morph.addScript(function onDragEnd() {
    this.mousePressed = false;

    if (this.mouseJoint)
    {
        this.box2dWorld.DestroyJoint(this.mouseJoint);
        this.mouseJoint = false;
    }
    });

    morph.addScript(function onDragStart() {
    this.mousePressed = true;
    });

    morph.addScript(function onOwnerChange() {
    var morphArr = lively.morphic.World.current().morphCollection;
    if (!this.owner)
    {
        this.box2dWorld.DestroyBody(this.physicsVersion);
        disconnect(this.physicsVersion.m_xf.position, "y", this, "setPosition");    
        disconnect(this.physicsVersion.m_xf.R.col1, "y", this, "setRotation");
        morphArr.splice(morphArr.indexOf(this), 1);
    }
    else if (this.owner === lively.morphic.World.current())
    {
        morphArr.push(this);
        var thisName = this.name;
        setTimeout(function(){lively.morphic.World.current().get(thisName).recreatePhysicsVersion(lively.morphic.World.current().get(thisName).getExtent());}, 20);    
    }
    });

    morph.addScript(function recreatePhysicsVersion(ext) {
    var pos = this.getPosition();  
    var replacement = null;
    if (this.getShape().constructor.type === "lively.morphic.Shapes.Rectangle")
        replacement = this.world().createBox(pos.x, pos.y, ext.x/2, ext.y/2, Box2D.Dynamics.b2Body.b2_dynamicBody, this.box2dWorld);
    replacement.m_mass /= 100; // allows easier throwing
    replacement.SetAngle(this.getRotation());
    if (this.physicsVersion)
    {
        replacement.SetLinearVelocity(this.physicsVersion.GetLinearVelocity());
        this.box2dWorld.DestroyBody(this.physicsVersion);
    }
    this.physicsVersion = replacement;
    if (this.physicsVersion)
    {    
        connect(this.physicsVersion.m_xf.position, "y", this, "setPosition", {converter:
    	    function converter(value) {
            return pt(this.sourceObj.x, value);
        }});    
        connect(this.physicsVersion.m_xf.R.col1, "y", this, "setRotation", {converter:
    	    function converter(value) {
            return Math.atan2(value, this.sourceObj.x);
        }});
    }
    });

    morph.world().morphCollection.push(morph);
    morph.disconnectBox2D();
    morph.connectBox2D();    
}

