function onLoadFromPartsBin() {
	$super();
	Trait('lively.morphic.DraggableBehavior').applyTo(this, {override: ['onDragStart', 'onDragEnd']});

	var targetWorld = lively.morphic.World.current();

	targetWorld.addScript(function animate() {
    if (!this.running)
        return;

 	requestAnimationFrame(this.animate.bind(this));

    var world = this.box2dWorld;
    world.Step(1/60,  10,  10);
    world.DrawDebugData();
    world.ClearForces();
    });

    targetWorld.addScript(function createBox(x, y, width, height, bodyType, world) {
    var   b2BodyDef = Box2D.Dynamics.b2BodyDef
        , b2FixtureDef = Box2D.Dynamics.b2FixtureDef
        , b2PolygonShape = Box2D.Collision.Shapes.b2PolygonShape;

    var fixDef = new b2FixtureDef;
    fixDef.density = 1.0;
    fixDef.friction = 0.5;
    fixDef.restitution = 0.2;
    var bodyDef = new b2BodyDef;
    bodyDef.type = bodyType;  
    fixDef.shape = new b2PolygonShape();
    fixDef.shape.SetAsBox(width, height);
    bodyDef.position.x = x;
    bodyDef.position.y = y;
    var a = world.CreateBody(bodyDef);
    a.CreateFixture(fixDef);
    return a;
    });

    targetWorld.addScript(function onLoad() {
    this.startBox2D();
    });

    targetWorld.addScript(function startBox2D() {
    var b2Canvas = document.createElement('canvas');
    b2Canvas.id = 'box2dCanvas';
    b2Canvas.width = this.world().windowBounds().width;
    b2Canvas.height = this.world().windowBounds().height;
    b2Canvas.style.backgroundColor = '#f0f0f0';
    this.renderContext().shapeNode.appendChild(b2Canvas);
    this.box2dCanvas = b2Canvas;

    var     b2Vec2 = Box2D.Common.Math.b2Vec2
     	,	b2Body = Box2D.Dynamics.b2Body
     	,	b2World = Box2D.Dynamics.b2World
     	,	b2DebugDraw = Box2D.Dynamics.b2DebugDraw;
    var world = new b2World(new b2Vec2(0, 10), true);

    this.mousePressed = false;
    this.mouseJoint = false;

    var debugDraw = new b2DebugDraw();
		debugDraw.SetSprite(b2Canvas.getContext("2d"));
		debugDraw.SetFillAlpha(0.3);
		debugDraw.SetLineThickness(1.0);
		debugDraw.SetFlags(b2DebugDraw.e_shapeBit | b2DebugDraw.e_jointBit     );
	world.SetDebugDraw(debugDraw);

    this.walls = [];
    var bounds = this.world().windowBounds();
    this.walls.push(this.createBox(bounds.width/2, bounds.height + 1, bounds.width, 1, b2Body.b2_staticBody, world));
    this.walls.push(this.createBox(bounds.width/2, 0, bounds.width, 1, b2Body.b2_staticBody, world));
    this.walls.push(this.createBox(0, bounds.height/2, 1, bounds.height, b2Body.b2_staticBody, world));
    this.walls.push(this.createBox(bounds.width + 1, bounds.height/2, 1, bounds.height, b2Body.b2_staticBody, world));

    this.box2dWorld = world;

    if (!this.morphCollection)
        this.morphCollection = [];

    for (var i = 0; i < this.morphCollection.length; i++)
    {
        this.morphCollection[i].disconnectBox2D();        
        this.morphCollection[i].connectBox2D();
    }

    this.running = true;
    this.animate();
    });

    require('lib.box2dWeb.Box2dWeb21a3.js').toRun(function() {
        targetWorld.startBox2D()
        alertOK('Box2D canvas has been created');    
    });
}

