function become(player) {
    if (this.character) {
        this.character.remove()
        this.character = null
    }
    if (!player) { return this }

    this.character = this.toolbox(player + "Character")
    if (this.character) {
        // image should be 150% of a tile
        this.character.setExtent(this.tileSize().scaleBy(1.5))
        // -25% offset to center character on tile
        this.character.setPositionTopLeft(this.tileSize().scaleBy(-0.25))
        this.addMorph(this.character)
        this.character.ignoreEvents()
    }
}

