function walkUntilReached(tile) {
    var minPositive = function (a, b) {
        a = Math.abs(a), b = Math.abs(b)
        return a ? (b ? Math.min(a, b) : a) : b
    }

    // this.setPosition(pt(0,0))
    if (this.getPosition().equals(tile.getPosition())) {
        if (this.walkScript) { this.walkScript.stop() }
        this.setDirection(pt(0,0))
        tile.enterTile(this)
    } else {
        if (this.currentTile() !== tile) {
            this.setDirectionToTile(tile)
            var nextNeighbours = this.currentTile().neighboursInDirection(this.getDirection())
            if (nextNeighbours.any(function (each) { return each.isObstacle() })) {
                this.walkTo(this.currentTile())
            } else {
                var directionVector = this.getPosition().subPt(tile.getPosition()),
                    maxSpeed = minPositive(directionVector.x, directionVector.y),
                    speed = Math.min(maxSpeed, this.getCurrentSpeed())
                this.moveBy(this.getDirection().scaleBy(speed))
            }
        } else {
            var directionVector = this.getPosition().subPt(tile.getPosition()),
                    maxSpeed = minPositive(directionVector.x, directionVector.y),
                    speed = Math.min(maxSpeed, this.getCurrentSpeed())
            this.moveBy(this.getDirection().scaleBy(speed))
        }
    }
}

