function shallowCopy() {
    var copy = Morph.makeRectangle(this.bounds()),
        that = this
    Functions.own(this).each(function (ea) {
        copy[ea] = that[ea].asScriptOf(copy, ea)
    })

    // also shallow-copy partsBinMetaInfo 
    if (this.partsBinMetaInfo) {
        copy.partsBinMetaInfo = this.partsBinMetaInfo
    }

    // full copy for death animation and life bar
    copy.lifeBar = this.lifeBar.copy()
    copy.lifeBar.character = copy
    copy.addMorph(copy.lifeBar)
    copy.deathAnimation = this.deathAnimation.copy()
    copy.deathAnimation.character = copy
    copy.addMorph(copy.deathAnimation)

    return copy
}

