function conversation(args) {
    var other = args.other, a, b, stopConversation
    if (this.conversationTurn) {
        a = this; b = other
    } else {
        a = other; b = this
    }
    [a,b].invoke("hideTextBubble")
    if (this.lastAnswer == null) {
        this.conversationScript.stop()
        this.lastAnswer = a.answer(undefined, b, true)
        if (this.lastAnswer) { a.say(this.lastAnswer) }
        var that = this
        setTimeout(function() {
            a.hideTextBubble()
            that.lastAnswer = b.answer(that.lastAnswer, a, true)
            if (that.lastAnswer) { b.say(that.lastAnswer) }
            a.isBusy = false
            b.isBusy = false
        }, 4000)
    }
    this.lastAnswer = a.answer(this.lastAnswer, b, false)
    if (this.lastAnswer) { a.say(this.lastAnswer) }
    this.conversationTurn = !this.conversationTurn
}

