function answer(lastSaid, other, sayGoodbye) {
    // either return a string or null to indicate that you want to stop the conversation
    // when sayGoodbye-flag is set, this is the last answer-call for this conversation
    var type = this.characterType()
    if ( type && type.answer ) {
        return type.answer(lastSaid, other, sayGoodbye)
    } else {
        if (sayGoodbye) {
            return "Goodbye!"
        } else {
            if ( Math.random() > 0.2 ) {
                return this.chatBot().answerTo(lastSaid);
            }
        }
    }

}

514.source:
function aStar(goal) {
    var that = this,
        start = this.currentTile(),
        closedset = [],
        openset = [start], // The set of tentative nodes to be evaluated
        came_from = {},    // The map of navigated nodes.
        g_score = {},      // general cost along best known path (as the bird flies)
        h_score = {},      // heuristic cost - used to drive parameters
        f_score = {}       // sum of heuristic and general cost

    g_score[start] = 0    // Cost from start along best known path.
    h_score[start] = this.aStarHeuristicCostEstimate(start, goal)
    f_score[start] = g_score[start] + h_score[start] // Estimated total cost from start to goal

    while (openset.length > 0) { // Search until we have nothing more to search
        // Pick the next best scored candidate
        var current = openset.sortBy(function (e) { return f_score[e] }).first()

        if (current === goal) { // we found a path, construct the whole path and return
            var path = [],
                current_node = current
            while (came_from[current_node]) {
                path.push(current_node)
                current_node = came_from[current_node]
            }
            path.push(current_node)
            return path.reverse()
        }

        // We haven't found a path, mark the candidate as checked
        openset = openset.without(current)
        closedset.push(current)

        // check all neighbours
        current.neighboursReachableFromHere().each(function (neighbour) {
            if (!closedset.member(neighbour)) {
                // this neighbour can be walked upon and we haven't checked it before
                // assign it a score via the current path
                var tentative_g_score = g_score[current] + current.distanceTo(neighbour).r(),
                    tentative_is_better = true

                if (!openset.member(neighbour)) {
                    // we haven't given this a score before, give it a heuristic score
                    openset.push(neighbour)
                    h_score[neighbour] = that.aStarHeuristicCostEstimate(neighbour, goal)
                } else if (tentative_g_score >= g_score[neighbour]) {
                    // we have given a better score before, this one can be dismissed
                    tentative_is_better = false
                }

                if (tentative_is_better === true) {
                    // the new score we found for this neighbour is better than the previous
                    // set the new score and the new path
                    came_from[neighbour] = current
                    g_score[neighbour] = tentative_g_score
                    f_score[neighbour] = g_score[neighbour] + h_score[neighbour]
                }
            }
        })
    }
    return []
}

