function aStarWalkPath(path) {
    var minPositive = function (a, b) {
        a = Math.abs(a), b = Math.abs(b)
        return a ? (b ? Math.min(a, b) : a) : b
    }

    var finalTile = path.last(),
        nextTile = path.first()

    // this.setPosition(pt(0,0))
    if (this.getPosition().equals(finalTile.getPosition())) {
        if (this.walkScript) { this.walkScript.stop() }
        this.setDirection(pt(0,1))
        this.currentTile().enterTile(this)
    } else {
        this.setDirectionToTile(nextTile)
        var directionVector = this.getPosition().subPt(nextTile.getPosition()),
            maxSpeed = minPositive(directionVector.x, directionVector.y),
            speed = Math.min(maxSpeed, this.getCurrentSpeed())
        this.moveBy(this.getDirection().scaleBy(speed))
        if (path.length > 1) { // remove all but the final tile,
                               // we need that one for positioning
            var idx = path.indexOf(this.currentTile())
            if (idx > -1) {
                path.removeAt(idx)
            }
        }
    }
}

