538.source:
function fight() {
    this.isBusy = true
    var persons = []
    this.currentTile().neighbours().each(function(tile) {
        persons.pushAll(tile.characters())
    })
    persons = persons.select(function(each) { return !each.isBusy })
    if (persons.length > 0) {
        var personForFight = persons[Math.floor(Math.random()*persons.length)]
        personForFight.isBusy = true
        if (Math.round(Math.random()) === 0) {
            this.executeFightTurnAsking(this, personForFight)
        } else {
            this.executeFightTurnAsking(personForFight, this)
        }
    } else {
        this.isBusy = false
    }
}

