function randomQuestions(count) {
    if (!count) { count = 8 }

    var choices = [],
        questions = Properties.own(this.fightPhrases())

    for (var i = 0; i < count; i++) {
        var idx = Math.round(Math.random() * questions.length)
        choices.push(questions[idx])
        questions.removeAt(idx)
    }

    return choices
}

550.source:
function randomAnswersFor(question, count) {
    if (!count) { count = 8 }

    var choices = [],
        phrases = this.fightPhrases(),
        answers = Properties.own(phrases).collect(function (q) {
            return phrases[q]
        })

    choices.push(phrases[question])
    answers.removeAt(Properties.own(phrases).indexOf(question))

    for (var i = 1; i < count; i++) {
        var idx = Math.round(Math.random() * answers.length)
        choices.push(answers[idx])
        answers.removeAt(idx)
    }

    return choices.sortBy(function (ea) { return Math.random() })
}

554.source:
function executeFightTurnAsking(asker, responder) {
    var menu = this.toolbox("FightMenu"),
        worldCenter = rect(pt(0, 0), pt(window.innerWidth, window.innerHeight)).center(),
        talkTimeout = 4000,
        nextTurnCallback = function (answer, correctAnswer) {
            if (correctAnswer === answer) {
                // switch turns
                asker.executeFightTurnAsking(responder, asker)
            } else {
                responder.getHit()
                if (!responder.isDead()) {
                    asker.executeFightTurnAsking(asker, responder)
                }
            }
        }

    // Check if the last looser is dead, first
    if (responder.isDead()) {
        responder.isBusy = false
        asker.isBusy = false
        asker.heal()
        asker.select()
        return
    }

    // Fight
    if (asker.character.isPlayer) {
        menu.setHeader("Choose your insult")
        menu.setList(asker.randomQuestions(8), function (str) {
            menu.remove()
            asker.say(str)
            setTimeout(function () {
                // the npc has a 1/3 chance of answering right
                var response = responder.randomAnswersFor(str, 3)[Math.round(Math.random() * 2)],
                    correctAnswer = responder.randomAnswersFor(str, 1)[0]
                responder.say(response)
                setTimeout(function () {
                    nextTurnCallback(response, correctAnswer)
                }, talkTimeout)
            }, talkTimeout)
        })
        asker.map().addMorph(menu)
        menu.centerAt(worldCenter)
    } else {
        var question = asker.randomQuestions(1)[0],
            answer = asker.randomAnswersFor(question, 1)[0]
        asker.say(question)
        setTimeout(function () {
            menu.setHeader("What do you reply to '" + question + "'?")
            menu.setList(responder.randomAnswersFor(question, 7), function (str) {
                menu.remove()
                responder.say(str)
                setTimeout(function () {
                    nextTurnCallback(str, answer)
                }, talkTimeout)
            })
            asker.map().addMorph(menu)
            menu.centerAt(worldCenter)
        }, talkTimeout)
    }
}
