1073.source:
function createListItem(item) {
    var textString = item.string
    var part = Morph.makeRectangle(0, 0, 10, 10)

    part.name = "MenuItem_" + textString
    part.item = item

    part.applyStyle({
        extent: pt(this.getExtent().x, this.listItemSize()),
        fill: new lively.morphic.LinearGradient(
            [
                {offset: 0, color: Color.rgb(253,253,253)},
                {offset: 1, color: Color.rgb(238,238,238)}
            ],
            'northSouth'
        ),
        resizeWidth: true,
        borderColor: Color.rgb(138,138,138)
    });

    part.onMouseUp = this.onItemMouseUp
    part.menu = this.owner
    part.list = this

    var text = new TextMorph(new Rectangle(0,0,this.getExtent().x,10))
    text.applyStyle({fill: null, borderWidth: 0})
    text.setFontSize(12)
    text.setTextColor(Color.rgb(47,47,47))
    text.setFontFamily("Helvetica, Arial, sans-serif")

    text.setPosition(pt(10,10))
    text.textString = textString

    text.emphasizeAll({fontWeight: 'bold'})
    text.disableHalos()
    text.ignoreEvents()
    part.addMorph(text)

    return part
}

