2787.source:
function createListItem(textString) {
    var part = lively.PartsBin.getPart("Rectangle", "PartsBin/Basic");

    part.index = this.itemList.length;

    part.setExtent(pt(this.getExtent().x,44));
    part.applyStyle({resizeWidth: true});
    part.setFill(new lively.morphic.LinearGradient(
        [
            {offset: 0, color: Color.rgb(253,253,253)},
            {offset: 1, color: Color.rgb(238,238,238)}
        ],
        'northSouth'
    ));
    part.setBorderColor(Color.rgb(138,138,138));



    part.onTouchStart = this.touchstartCallback;
    part.onTouchMove = this.touchmoveCallback;
    part.onTouchEnd = this.touchendCallback;

    part.registerForEvent("touchstart", part, "onTouchStart", false);
    part.registerForEvent("touchmove", part, "onTouchMove", false);
    part.registerForEvent("touchend", part, "onTouchEnd", false);

    var text = lively.PartsBin.getPart("Text", "PartsBin/Basic");
    text.setFontSize(14);
    text.setTextColor(Color.rgb(47,47,47));
    text.setFontFamily("Helvetica, Arial, sans-serif");

    text.setPosition(pt(10,10));
    text.textString = textString;

    text.emphasizeAll({fontWeight: 'bold'});
    text.disableHalos();
    part.addMorph(text);

    return part;
}

