3497.source:
function createListItem(textString) {
    var part = lively.PartsBin.getPart("Rectangle", "PartsBin/Basic");

    part.index = this.itemList.length;

    part.setExtent(pt(this.getExtent().x,44));
    part.applyStyle({resizeWidth: true});
    part.setFill(new lively.morphic.LinearGradient(
        [
            {offset: 0, color: Color.rgb(253,253,253)},
            {offset: 1, color: Color.rgb(238,238,238)}
        ],
        'northSouth'
    ));
    part.setBorderColor(Color.rgb(138,138,138));



    part.onTouchStart = this.touchstartCallback;
    part.onTouchMove = this.touchmoveCallback;
    part.onTouchEnd = this.touchendCallback;

    part.registerForEvent("touchstart", part, "onTouchStart", false);
    part.registerForEvent("touchmove", part, "onTouchMove", false);
    part.registerForEvent("touchend", part, "onTouchEnd", false);

    var text = lively.PartsBin.getPart("Text", "PartsBin/Basic");
    text.setFontSize(14);
    text.setTextColor(Color.rgb(47,47,47));
    text.setFontFamily("Helvetica, Arial, sans-serif");

    text.setPosition(pt(10,10));
    text.textString = textString;

    text.emphasizeAll({fontWeight: 'bold'});
    text.disableHalos();
    part.addMorph(text);

    return part;
}

3501.source:
function updateSelection(newSelectedMorph) {
    var hasText = true;
    if(this.selectedMorph) {
        hasText = this.selectedMorph.submorphs[0];
        this.selectedMorph.setFill(
            new lively.morphic.LinearGradient(
            [
                {offset: 0, color: Color.rgb(253,253,253)},
                {offset: 1, color: Color.rgb(238,238,238)}
            ],
            'northSouth'
            )
        );
        if(hasText) {        this.selectedMorph.submorphs[0].setTextColor(Color.rgb(47,47,47));
        }
    }
    hasText = newSelectedMorph.submorphs[0];


    if(hasText) {
        this.selection = newSelectedMorph.submorphs[0].textString;
    } else {
        this.selection = "";
    }
    this.selectedLineNo = newSelectedMorph.index;
    this.selectedMorph = newSelectedMorph;

    this.selectedMorph.setFill(
new lively.morphic.LinearGradient(
        [
            {offset: 0, color: Color.rgb(47,47,47)},
            {offset:0.5,color: Color.rgb(21,21,21)},
            {offset: 1, color: Color.rgb(0,0,0)}
        ],
        'northSouth'
    ));

    if(hasText) {
        this.selectedMorph.submorphs[0].setTextColor(Color.rgb(222,222,222));
    }
}

3505.source:
function handleClick() {
    switch(this.selection){
        case "PartsBin":
            $world.openPartsBin();
            $world.hideMockMenu();
            break;
        case "Parts":
            this.openSubmenuWith(
                ["Ellipse","Image","Line","Rectangle","Text",
                "Button","List","ScriptableButton","Slider"]
            );
            break;
        case "Ellipse":
        case "Image":
        case "Line":
        case "Rectangle":
        case "Text":
            $world.openPartItem(this.selection, "PartsBin/Basic");
            $world.hideMockMenu();
            break;
        case "Button":
        case "List":
        case "ScriptableButton":
        case "Slider":
            $world.openPartItem(this.selection, "PartsBin/Inputs");
            $world.hideMockMenu();
            break;
        case "Tools":
            this.openSubmenuWith(
                ["Workspace","System Code Browser","Object Editor",
                "Method Finder","Text Editor"]
            );
            break;
        case "Workspace":
            $world.openWorkspace();
            $world.hideMockMenu();
            break;
        case "System Code Browser":
            $world.openSystemBrowser();
            $world.hideMockMenu();
            break;
        case "Object Editor":
            $world.openObjectEditor();
            $world.hideMockMenu();
            break;
        case "Method Finder":
            $world.openMethodFinder();
            $world.hideMockMenu();
            break;
        case "Text Editor":
            new lively.morphic.TextEditor().openIn(world)
            $world.hideMockMenu();
            break;
        case "Preferences":
            this.openSubmenuWith(
                ["set username","set extent","disable part caching"]
            );
            break;
        case "set username":
            $world.askForUserName();
            $world.hideMockMenu();
            break;
        case "set extent":
            $world.askForNewWorldExtent();
            $world.hideMockMenu();
            break;
        case "disable part caching":
            $world.hideMockMenu();
            break;
        case "Debugging":
            this.openSubmenuWith(
                ["reset world scale","reset title bars","reset button labels",
                "World serialization info","clear part cache","show connectors"]
            );
            break;
        case "reset world scale":
            $world.resetScale();
            $world.hideMockMenu();
            break;
        case "reset title bars":
            $world.resetAllTitleBars();
            $world.hideMockMenu();
            break;
        case "reset button labels":
            $world.resetAllButtonLabels();
            $world.hideMockMenu();
            break;
        case "World serialization info":
            require('lively.persistence.Debugging').toRun(
                function() {
                    var json = lively.persistence.Serializer.serialize(world),
                        printer = lively.persistence.Debugging.Helper.listObjects(json);
                    world.addTextWindow(printer.toString());
                }
            );
            $world.hideMockMenu();
            break;
        case "clear part cache":
            $world.hideMockMenu();
            break;
        case "show connectors":
            $world.hideMockMenu();
            break;
        case "Wiki":
            this.openSubmenuWith(
                ["about this wiki","view versions of this world","download world",
                "upload world to Dropbox","delete world"]
            );
            break;
        case "about this wiki":
            $world.openAboutBox();
            $world.hideMockMenu();
            break;
        case "view versions of this world":
            $world.openVersionViewer();
            $world.hideMockMenu();
            break;
        case "download world":
            require('lively.persistence.StandAlonePackaging').toRun(
                function() {
                    lively.persistence.StandAlonePackaging.packageCurrentWorld();
                }
            );
            $world.hideMockMenu();
            break;
        case "upload world to Dropbox":
            require('apps.Dropbox').toRun(
                function() {
                    DropboxAPI.uploadArchivedWorld();
                }
            );
            $world.hideMockMenu();
            break;
        case "delete world":
            $world.interactiveDeleteWorldOnServer();
            $world.hideMockMenu();
            break;
        case "Documentation":
            this.openSubmenuWith(
                ["on short cuts","on connect data bindings",
                "on Lively's PartsBin","more..."]
            );
            break;
        case "on short cuts":
            $world.openShortcutDocumentation();
            $world.hideMockMenu();
            break;
        case "on connect data bindings":
            $world.openConnectDocumentation();
            $world.hideMockMenu();
            break;
        case "on Lively's PartsBin":
            $world.openPartsBinDocumentation();
            $world.hideMockMenu();
            break;
        case "more...":
            window.open(Config.rootPath + 'documentation/');
            $world.hideMockMenu();
            break;
        case "save world as ...":
            $world.interactiveSaveWorldAs();
            $world.hideMockMenu();
            break;
        case "save world":
            $world.saveWorld();
            $world.hideMockMenu();
            break;
        default:
            //Do nothing
    }
}

