function onTouchStart(evt) {
    evt.stop();

    var touch = evt.touches[0];

    if(touch) {
        touch.originalDragOffset = touch.clientY;
        touch.originalMenuOffset = this.getPosition().y;

        this.isTouched = true;
        this.velocity = 0;
        touch.lastTouch = touch.clientY;
        touch.lastUpdate = new Date().valueOf();
    }
    return true;


}

