4081.source:
function onTouchMove(evt) {
    evt.stop();

    var touch = evt.touches[0];

    if(touch && touch.originalDragOffset) {
        //TODO: Store this attributes in the object to save computation time
        var heightMenu = this.owner.itemList.length*43;
        var heightContainer = this.owner.getExtent().y;
        var maxScroll = heightMenu-heightContainer;


        var delta = touch.clientY - touch.originalDragOffset;
        this.setPosition(pt(0,touch.originalMenuOffset+delta));

        if(!this.isInBounds()) {
            var yPos = Math.min(0,Math.max(-maxScroll, this.getPosition().y));
            delta = this.getPosition().y-yPos;
            this.moveBy(pt(0,-delta/2));
        }

        var positionDelta = touch.lastTouch-touch.clientY;
        var now = new Date().valueOf();
        var timeDelta = now - touch.lastUpdate;

        touch.lastTouch = touch.clientY;
        touch.lastUpdate = now;

        this.velocity = positionDelta*(-10/timeDelta);


    }
    return true;

}

