4089.source:
function onTouchEnd(evt) {
    $world.get("TouchList").submorphs[0];
    var lastUpdate = new Date().valueOf();
    this.isTouched = false;
    var that = this;

    var flickingFct = function() {
        var now = new Date().valueOf();
        var delta = now - lastUpdate;
        var velo;
        that.velocity *= Math.pow(0.99, delta);
        that.moveBy(pt(0,that.velocity));
        that.stayInBounds();
        velo = that.velocity;

        lastUpdate = now;
        if((velo*velo>0.05 || !that.isInBounds()) && !that.isTouched) {
            window.setTimeout(flickingFct,10);
        }
    };

    window.setTimeout(flickingFct,10);

}

