4179.source:
function setList(ary, callback) {
    if (!callback) {
        callback = function (s) { alertOK('Clicked "' + s + '"') }
    }
    var touchListAry = ary.collect(function (str) {
        return [str, function () { callback(str) }]
    })
    this.get("TouchList").setup(touchListAry)
    this.get("TouchList").snapAroundItems()

    var newHeight = this.get("TouchList").getExtent().y + this.submorphs[0].getExtent().y

    this.setExtent(lively.pt(this.getExtent().x, newHeight))

    this.get("TouchList").snapAroundItems() // todo: why twice?
}
