function getTileForPosition(point) {
    var column = Math.floor(point.x / this.tileSize().x),
        row = Math.floor(point.y / this.tileSize().y)

    return this.tiles[column][row]
}

167.source:
function setupTiles() {
    this.tiles = []
    for(var column = 0; column < this.mapTileSize().x; column++) {
        this.tiles[column] = []
    }
    for(var row = 0; row < this.mapTileSize().y; row++) {
        for(var column = 0; column < this.mapTileSize().x; column++) {
            var currentTile = this.toolbox("Tile")
            this.tiles[column][row] = currentTile
            currentTile.setGridPosition(column, row)
            currentTile.setNormalStyle()
            this.addMorph(currentTile)
            if (Math.random() < 0.04 ) {
                currentTile.become("FlowerGrassTile")
            } else {
                currentTile.become("GrassTile")
            }
            currentTile.disableGrabbing()
        }
    }
}

171.source:
function setupUnits() {
    var numberOfUnits = Math.round(Math.random() * 5 + 5),
        xPosition, yPosition,
        maxX = this.mapTileSize().x - 4,
        maxY = this.mapTileSize().y - 4

    for (var x = 0; x <= numberOfUnits; x++) {
        xPosition = Math.round(Math.random() * maxX + 2)
        yPosition = Math.round(Math.random() * maxY + 2)
        var currentUnit = this.toolbox("Character"),
            tile
        this.addMorph(currentUnit)
        currentUnit.setNormalStyle()
        tile = this.tiles[xPosition][yPosition]
        currentUnit.setPosition(tile.getPosition())
        tile.enterTile(currentUnit)
        x === 0 ? currentUnit.becomePlayer() : currentUnit.becomeRandomCharacter()
        currentUnit.animate()
    }
}

175.source:
function setupThings() {
    var numberOfThings = Math.round(Math.random() * 5 + 5),
        xPosition, yPosition,
        maxX = this.mapTileSize().x - 4,
        maxY = this.mapTileSize().y - 4

    for (var x = 0; x <= numberOfThings; x++) {
        xPosition = Math.round(Math.random() * maxX + 2)
        yPosition = Math.round(Math.random() * maxY + 2)
        var currentThing = this.toolbox("Thing")
        currentThing.dropOn(this.tiles[xPosition][yPosition])
        currentThing.setNormalStyle()
        currentThing.becomeRandomThing()
    }
}

