function loadPartItem(partName) {
    if (!this.partItemCache) this.partItemCache = {}

    var shallowCopyableItem = this.partItemCache[partName]
    if (shallowCopyableItem) {
        return shallowCopyableItem.shallowCopy()
    } else {
        var newItem = $world.loadPartItem(partName, 'PartsBin/' + this.getPartsBinCategory())
        if (typeof newItem.shallowCopy == "function") {
            this.partItemCache[partName] = newItem
            return newItem.shallowCopy()
        } else {
            return newItem
        }
    }
}

