function partNamesEndingWith(string) {
    if (!this.partNamesCache) this.partNamesCache = {}
    if (this.partNamesCache[string]) return this.partNamesCache[string]

    this.partNamesCache[string] = lively.PartsBin.
                    partsSpaceNamed("PartsBin/" + this.getPartsBinCategory() + "/").
                    getPartNames().select(function (each) {
                        return each.match(new RegExp(".+" + string + "$"))
                    }).collect(function (each) {
                         return each.truncate(each.length - string.length, "")
                    });
    return this.partNamesCache[string]
}
