function refresh() {
    var that = this;
    var webR = new WebResource('http://lively-kernel.org/nodejs/FabiansPlayground/lastMessages').beAsync();
    connect(webR, 'content', this, 'refreshed', {
    updater: function($upd, content) {
        if (this.sourceObj.status.isDone())
            $upd(content) }})
    webR.get()

}

