function refreshed(content) {
    var messages = JSON.parse(content);
    var that = this;
    this.submorphs.forEach(function(ea) {ea.remove()});
    messages.forEach(function(ea) {
        if (!ea) {return;}
        var displayStr = ea.user + ': ' + ea.message;
        //var text = new TextMorph(new Rectangle(0, that.getExtent().y, 400, 20), displayStr);
        var text = new TextMorph(new Rectangle(0, 0, 400, 20), displayStr);
        text.layout = {resizeWidth: true};
        that.get('Messages').addMorph(text);
    });
    this.owner.applyLayout();
}

