function post() {
    var msg =  this.get('Message').getTextString();
    if (!msg) { return; }
    var o = new Object();
    o.user = this.get('Username').getTextString();
    o.message = msg;
    var json = JSON.stringify(o);
    new WebResource('http://lively-kernel.org/nodejs/FabiansPlayground/postMessage').post(json);
    this.get('Message').setTextString('');
    this.refresh();
}
