function shuffle() {
    var tmp, rand;
    for(var i= 0; i < 100; i++) {
        random1 = Math.floor(Math.random() * this.cards.length);
        random2 = Math.floor(Math.random() * this.cards.length);
        // alertOK("tausche " + random1 + " mit " + random2)
        tmp = this.cards[random1];
        this.cards[random1] = this.cards[random2];
        this.cards[random2] = tmp;
   }
}

