function testTakeCard() {
    // this.testTakeCard()
    this.newSet()
    this.shuffle()    
    this.showCards()

    assert(this.cards.length == 32, "wrong card number")
    this.takeCard();
    assert(this.cards.length == 31, "wrong number")

    var card = this.takeCard("hearts", "queen")
    assert(card.cardColor == "hearts", "wrong color")
    assert(card.cardValue == "queen", "wrong value")
}

