function dealCards() {
    this.humanPlayer = this.get('MyCardstack');
    this.dealCardsOnStack( this.get('MyCardstack'), 8);
    this.dealCardsOnStack( this.get('CPUCardstack1'), 8);
    this.dealCardsOnStack( this.get('CPUCardstack2'), 8);
    this.dealCardsOnStack( this.get('CPUCardstack3'), 8);    
    this.searchClubsSeven(); 
}

966.source:
function dealCardsOnStack(cardStack, n) {
    var x = 0;
     while(n > 0) {
        var card = this.get('CardStack').cards.pop();
        if(card.cardColor == 'clubs' && card.cardValue == 'seven') {
            this.currentPlayer = cardStack;
        }  
        card.remove();
        cardStack.addMorph(card);
        card.setPosition(pt(x,10));
        x = x + 35; 
        n--;
    } 
}

970.source:
function playCard(player, card) {
    if(!card) {
        this.log("I cant play any card!");
        return;
    }
    this.log("playCard " + player + " " + card)
    if(this.isCorrectCard(card)) {
        this.get('CurrentCards').addMorph(card);
        card.setPosition(pt(0,0));
        this.currentCard = card;
        this.currentPlayer = this.playerAfter(player);
        this.log("next " + this.currentPlayer);
        this.playerTakesTurn(this.currentPlayer);
    } else {
        this.log("That doesnt work!");           
    }
}

974.source:
function playerTakesTurn(player) {
    (function() {
        this.log("player " + player + " takes turn");

        this.log(this.humanPlayer);

        if(player != this.humanPlayer) {
            card = this.playerChooseCards(player)
            this.playCard(player, card)        
        } else {
            this.log("Your Turn!");
        }

    }).bind(this).delay(1)
}

986.source:
function playerAfter(player) {
    var currentIndex = this.playerNames().indexOf(player.getName());
    var nextPlayerName = this.playerNames()[(currentIndex + 1) % this.playerNames().length];
    return this.get(nextPlayerName);

}

