1010.source:
function playerChooseCards(player) {
    var color = this.getCurrentCard().cardColor
    var validCards = player.submorphs.select(function(card) {
        return card.cardColor == color;
    });
    if(validCards.length > 0) {
        return validCards[0];
    } else {
        return player.submorphs[0];
    }




}

