function testPlayerChooseCards() {
    // this.testPlayerChooseCards()
    var cardStack = this.get('CardStack')
    cardStack.newSet();

    this.currentCard = cardStack.takeCard('hearts', 'queen' )

    var player = this.get('CPUCardstack1');
    player.removeAllMorphs()

    player.addMorph(cardStack.takeCard('hearts', 'king' ))
    player.addMorph(cardStack.takeCard('clubs', 'queen' ))
    player.addMorph(cardStack.takeCard('clubs', 'ace' ))

    var card =  this.playerChooseCards(player);
    assert(card.cardColor == 'hearts', "wrong color")

}

